import styled from 'styled-components';
import {
  CheckActiveIcon,
  CheckCircleIcon,
  CheckHoverIcon,
} from '../assets/images';
import clsx from 'clsx';
import { useRef } from 'react';

const StyledTaskItem = styled.div`
  min-height: 52px;
  display: flex;
  align-items: center;
  position: relative;
  word-wrap: break-word;
  word-break: break-word;
  padding: 0 12px;
  box-shadow: 0 17px 0 -16px #e5e5e5;
  flex-wrap: wrap;

  .task-item-body-input {
    user-select: none;
    display: none;
    flex: 1;
    padding: 8px 0px;
    border: 0;
    outline: 0;
    font-size: 1rem;

    &::placeholder {
      color: var(--gray);
      font-size: 13px;
    }
  }

  &:hover {
    background: #fff3eb;
    box-shadow: inset 0 0 0 1px #fff3eb;

    .task-item-action .btn-destroy {
      display: inline-flex;
    }
  }

  &.done {
    .task-item-body {
      color: var(--gray);
      text-decoration: line-through;
    }

    .icon-checked {
      background-image: url(${CheckActiveIcon});
    }
  }

  &.edit {
    .task-item-body-input {
      display: block;
    }
    .task-item-body-text {
      display: none;
    }
    .task-item-action {
      display: none !important;
    }
  }

  .task-item-checked {
    display: flex;
    align-items: center;
    justify-content: center;
  }

  .task-item-body {
    font-weight: 400;
    padding: 8px 12px;
    flex: 1;
    display: flex;
  }

  .task-item-action {
    .btn-destroy {
      display: none;
      font-size: 30px;
      transition: color 0.2s ease-out;
      font-weight: 300;
      &:after {
        content: '×';
      }
    }
  }

  .icon-checked {
    background-image: url(${CheckCircleIcon});
    background-position: center;
    background-repeat: no-repeat;

    &:hover {
      transition: background-image 0.5s;
      background-image: url(${CheckHoverIcon});
    }
  }
`;

/*TodoItem裡面的props直接沿用TodoCollection的props*/
const TodoItem = ({todo, onSave, onDelete, onToggleDone, onChangeMode}) => {
  const inputRef = useRef(null)
  //製作監聽器,在編輯模式中監聽不同event.key的流程
  function handleKeyDown(event){
    if(inputRef.current.value.length>0 && event.key === 'Enter'){
      console.log('onSave triggered!')
      onSave({id:todo.id, title:inputRef.current.value})
    }

    if(event.key === 'Escape'){
      onChangeMode({id:todo.id, isEdit:false})
    }
  }
  return (
    <StyledTaskItem
      className={clsx('', { done: todo.isDone, edit: todo.isEdit })}
    >
      <div className="task-item-checked">
        <span
          className="icon icon-checked"
          onClick={() => onToggleDone(todo.id)}
        />
      </div>
      <div
        className="task-item-body"
        onClick={() => {
          console.log('double Click triggered!');
          onChangeMode?.({ id: todo.id, isEdit: true });
        }}
      >
        <span
          className="task-item-body-text"
        >
          {todo.title}
        </span>
        <input
          ref={inputRef}
          className="task-item-body-input"
          defaultValue={todo.title}
          onKeyDown={handleKeyDown}
        />
      </div>
      <div className="task-item-action ">
        <button className="btn-reset btn-destroy icon"
        onClick={()=>{
          onDelete(todo.id)}}></button>
      </div>
    </StyledTaskItem>
  );
};

export default TodoItem;
