import {
  AuthContainer,
  AuthInputContainer,
  AuthButton,
  AuthLinkText,
} from "../components/common/auth.styled";
import { AuthInput } from "../components";
import { ACLogoIcon } from "../assets/images";
import { useState } from 'react'

const LoginPage = () => {
  //用state管理onchange變化
  const [username, setUsername] =states('')
  const [password, setPassword] =states('')

  function handleAccountOnChange(event){
    setUsername(event.target.value);
  }
  function handlePassWordOnChange(event) {
    setPassword(event.target.value);
  }

  return (
    <AuthContainer>
      <div>
        <ACLogoIcon />
      </div>
      <h1>登入 Todo</h1>

      <AuthInputContainer>
        <AuthInput
          label="帳號"
          placeholder="請輸入帳號"
          type="text"
          value={username}
          onChange={handleAccountOnChange}
        />
      </AuthInputContainer>

      <AuthInputContainer>
        <AuthInput
          label="密碼"
          placeholder="請輸入密碼"
          type="password"
          value={password}
          onChange={handlePassWordOnChange}
        />
      </AuthInputContainer>
      <AuthButton>登入</AuthButton>
      <AuthLinkText>註冊</AuthLinkText>
    </AuthContainer>
  );
};

export default LoginPage;
